import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';
import axios from 'axios';

function App() {
  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost/servicios_rest/login', { usuario: 'usuario', clave: 'contraseña' });
      if (response.data.success) {
        alert('Login exitoso');
      } else {
        alert('Login fallido');
      }
    } catch (error) {
      console.error('Error en login:', error);
    }
  };

  const handleVerArticulo = async () => {
    try {
      const response = await axios.get('http://localhost/servicios_rest/');
      console.log('Datos del producto:', response.data);
    } catch (error) {
      console.error('Error al obtener el producto:', error);
    }
  };

  return (
    <div>
      <h3>Bienvenido a la página web de la Actividad 3</h3>
      <div>
        <p>Rellente los campos para acceder</p>
        <p>
          <label>Usuario:</label>
          <input type="text"/>
        </p>
        <p>
          <label>Contraseña:</label>
          <input type="password"/>
        </p>
        <Button color={"primary"} onClick={handleLogin}>Login</Button>
      </div>
      <br/>
      <br/>
      <Button color={"primary"} onClick={handleVerArticulo}>Ver Detalles del artículo 1</Button>
    </div>
  );
}

export default App;
