import axios from 'axios';

function App() {
  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost/login', { usuario: 'test', clave: '1234' });
      if (response.data.success) {
        alert('Login exitoso');
      } else {
        alert('Login fallido');
      }
    } catch (error) {
      console.error('Error en login:', error);
    }
  };

  const handleVerArticulo = async () => {
    try {
      const response = await axios.get('http://localhost/productos?id_producto=1');
      console.log('Datos del producto:', response.data);
    } catch (error) {
      console.error('Error al obtener el producto:', error);
    }
  };

  return (
    <div>
      <Button onClick={handleLogin}>Login</Button>
      <Button onClick={handleVerArticulo}>Ver Art√≠culo 1</Button>
    </div>
  );
}

export default App;
