import React, { Component } from 'react';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Botonera(props){

  let lista = []
  for (let i = 0; i < props.listaBotones.length; i++) {
    for (let j = 0; j < props.listaBotones[i].length; j++) {
      if(props.listaBotones[i][j].mostrar===true){
        lista.push(<Button onClick={()=>props.clicar(i,j)} color={props.listaBotones[i][j].color} disabled={props.listaBotones[i][j].disabled}>{props.listaBotones[i][j].numero}</Button>)
      }else{
        lista.push(<Button onClick={()=>props.clicar(i,j)} color={props.listaBotones[i][j].color} disabled={props.listaBotones[i][j].disabled}></Button>)
      }
    }
    lista.push(<br/>)
  }

  if(props.play){
    return(lista)
  }
}

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      listaBotones:[],
      playable:false,
      fila:-1,
      columna:-1
    };
  }

  aleatorio(min,max){
    var horquilla = max - min;
    return Math.round(Math.random()*horquilla + min);
  }

  jugar(){
    let play = this.state.playable;
    play=true;

    let copiaLista = this.state.listaBotones;

    for (let i =0; i < 9; i++){
      let lista=[]
        for (let j = 0; j < 9; j++) {
          let aleatorio = this.aleatorio(1,9);
          lista.push({color:"secondary", disabled:false, numero:aleatorio, mostrar:false})
        }
      copiaLista.push(lista)
    }

    this.setState({listaBotones:copiaLista,playable:play});
  }

  clicar(i,j){
    let copiaLista = this.state.listaBotones
    let copiaFila = this.state.fila
    let copiaColumna = this.state.columna


    copiaLista[i][j].color="primary";
    copiaLista[i][j].mostrar=true;

    if(copiaFila!==-1){
      if(copiaLista[i][j].numero===copiaLista[copiaFila][copiaColumna].numero){
        copiaLista[i][j].color="success";
        copiaLista[i][j].mostrar=true;
        copiaLista[copiaFila][copiaColumna].color="success";
        copiaLista[copiaFila][copiaColumna].mostrar=true;

      }else{
        copiaLista[i][j].color="secondary";
        copiaLista[i][j].mostrar=false;
        copiaLista[copiaFila][copiaColumna].color="secondary";
        copiaLista[copiaFila][copiaColumna].mostrar=false;

      }
    }



    this.setState({listaBotones:copiaLista,fila:i,columna:j})

  }

  render(){
    return(
      <div className="App">
        <header className="App-header">
          <Button onClick={()=>this.jugar()}>Jugar</Button>
          <br/>
          <Botonera play={this.state.playable} listaBotones={this.state.listaBotones} clicar={(i,j)=>this.clicar(i,j)}/>
        </header>
      </div>
    )
  }
}
export default App;
