  return (
    <>
      <h2></h2>
      <ul>
      {copia.map(u=> {
        return(<li>
          {u.nombre} - {u.apellidos} - {u.telefono} 
        </li>)
      })}
    </ul>
    </>
  );
}


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contactos: [
       {nombre:"Victor", apellidos:"Mena", telefono:"123"},
      ],
    };
  }


  render() {
    return (
      <div className="App">
       <Lista usuarios={this.state.contactos}/>
      </div>
    );
  }
}
export default App;
