import React, { Component } from 'react';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Botonera(props){

  
  if(props.playable){
    
    return(<><p>Hola</p></>);
  }
}

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      listaBotones: [],
      playable:false,
    };
  }

  jugar(){
    let copPlayable = this.state.playable;
    let copLista = this.state.listaBotones;
    if(copPlayable){
      copPlayable=false;
    }else{
      copPlayable=true;
    }
    

    for (let i = 0; i < 20; i++) {
      copLista.push({color:"secondary"});
    }

    for (let j = 0; j < 12; j++) {
      copLista.push({color:"success"});
    }

    this.setState({listaBotones:copLista,playable:copPlayable});

  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <Button onClick={()=>this.jugar()}>Jugar</Button>
          <Botonera listaBotones={this.state.listaBotones} playable={this.state.playable}/>
        </header>
      </div>
    );
  }
}

export default App;
