import './App.css';
import { Component } from 'react';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      euros: 0,
      dolares: 0,
      forumula: 1.20,
    };
  }

  aumentar() {
    let aux = this.state.euros + 1
    this.setState({ euros: aux });
    this.actualizar()
  }

  disminuir() {
    let aux = this.state.euros - 1
    if(aux>=0){
      this.setState({ euros: aux });
    }
    this.actualizar();
  }

  actualizar(){
    let dolar = this.state.euros*this.state.forumula;
    this.setState({
      dolares:dolar
    })
    
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <h2>{this.state.euros} EUROS</h2>
            <button onClick={()=>this.aumentar()}>
              aumentar
            </button>
            <button onClick={()=>this.disminuir()}>
              disminiuir
            </button>
            <div>Equivale a {this.state.dolares} DÃ³lares</div>
          </div>
        </header>
      </div>
    );
  }
}

export default App;
