import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Alert } from 'reactstrap';
import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      estadoAlerta: false, // Controla si la alerta se muestra o no
    };
  }

  cambiarEstadoAlerta = () => {
    this.setState({ estadoAlerta: !this.state.estadoAlerta }); // Alterna el estado
  };

  render() {
    return (
      <div className="App">
        <Button color="primary" onClick={this.cambiarEstadoAlerta}>
          Click me!
        </Button>
        <Button color="danger">Click me!</Button>
        {this.state.estadoAlerta && (
          <Alert color="info" toggle={this.cambiarEstadoAlerta}>
            Soy una alerta Azul!
          </Alert>
        )}
        {this.state.estadoAlerta && (
          <Alert color="info" toggle={this.cambiarEstadoAlertaRojo}>
            Soy una alerta Azul!
          </Alert>
        )}
      </div>
    );
  }
}

export default App;
