import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Component } from 'react';
import { Button } from 'reactstrap';

class App extends Component {
  constructor(props) {

    super(props);
    this.state = {
      contador: 0,
      colores: Array(5).fill("secondary"),
      memoria: [],
    };
  }
  accionBtn(num) {
    const copiaColores = [...this.state.colores]
    const copiaContador = [...this.state.contador]
    const copiaMemoria = [...this.state.memoria]

    if (!copiaMemoria.includes(num)) {
      copiaMemoria.push(num)
      copiaColores[num] = "danger"
      copiaContador++

      this.setState({
        contador: copiaContador,
        colores: copiaColores,
        copiaMemoria: copiaMemoria
      })
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            {this.state.contador}
          </div>
          <div>
            <Botoncillo color={this.state.colores[0]} onClick={this.accionBtn(0)}/>
          </div>
        </header>
      </div>
    );
  }
}

function Botoncillo(props) {
  return (
    <Button color={props.color} onClick={props.accionBtn}></Button>
  );
}

export default App;
