import React from 'react';
import ReactDOM from 'react-dom';
import {Button} from 'reactstrap';
import "bootstrap/dist/css/bootstrap.min.css";

class Saluda extends React.Component{
  constructor(){
    super();
    this.state = {Saludo: "Selecciona el botón para cambiar de color"};
    this.mensaje_ingles = this.mensaje_ingles.bind(this)
    this.mensaje_aleman = this.mensaje_aleman.bind(this)
    this.mensaje_español = this.mensaje_español.bind(this)

  }
  accion_azul(){this.setState({Saludo: "Hello"})}
  accio_orjo(){this.setState({Saludo: "Halo"})}

  render(){
    return(
      <div>
        <h3>{this.state.Saludo}</h3>
        <Button onClick={this.mensaje_ingles}>Neutro</Button>
        <Button onClick={this.mensaje_aleman}>Azúl</Button>
        <Button onClick={this.mensaje_español}>Rojo</Button>
      </div>
    )
  }
}


ReactDOM.render(<Saluda/>, document.getElementById("root"));
