import React, { Component } from 'react';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Botonera(props) {
    let lista = [];
    for (let i = 0; i < 9; i++) {
      for (let j = 0; j < props.listaBotones[i].length; j++) {
        lista.push(<Button key={i*10+j}color={props.listaBotones[i][j].color} pulsado={props.listaBotones[i][j].pulsado}/>)
      }
      lista.push(<br/>)      
    }
    return <>{lista}</>;
  
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listaBotones: [], // Inicializamos como array vacío
      colores:["success","danger", "warning","secondary"]
    };
  }

  componentDidMount() {
    this.montar(); // Llamamos a montar después de que el componente se monte
  }
  montar(){
    let copiaLista = this.state.listaBotones;

    for (let i = 0; i < 9; i++) {
      let lista =[]
      for (let j = 0; j < 9; j++) {
        lista.push({color:"primary", pulsado:false});
      }
      copiaLista.push(lista)
    }

    this.setState({listaBotones:copiaLista});
  }

  clicar(i, j) {

  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Botonera clicar={(i,j)=>this.clicar(i,j)} listaBotones={this.state.listaBotones} />
        </header>
      </div>
    );
  }
}

export default App;
