import React, { Component } from 'react';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Botonera(props) {
  if (props.playable) {
    let lista = [];

    for (let i = 0; i < props.listaBotones.length; i++) {
      for (let j = 0; j < props.listaBotones[i].length; j++) {
        let colorFicha = "";
        if (props.listaBotones[i][j]) {
          colorFicha = "secondary";
        } else {
          colorFicha = "success";
        }
        if (i % 2 === 0) {
          lista.push(<Button color={colorFicha} key={`${i}-${j}-even`} />);
          lista.push(<Button outline key={i+j} />);
        } else {
          lista.push(<Button outline key={i+j} />);
          lista.push(<Button color={colorFicha} key={`${i}-${j}-odd`} />);
        }
      }
      lista.push(<br key={`br-${i}`} />);
    }
    return <>{lista}</>;
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listaBotones: [], // Inicializamos como array vacío
      playable: false,
    };
  }

  

  jugar() {
    let copPlayable = this.state.playable;
    let copLista = [];

    if (!copPlayable) {
      // Creamos la lista de botones dinámicamente
      for (let i = 0; i < 8; i++) {
        if (i < 5) {
          copLista.push(Array(4).fill(true)); // Fichas "libres"
        } else {
          copLista.push(Array(4).fill(false)); // Fichas "ocupadas"
        }
      }
      copPlayable = true;
    } else {
      // Si ya está en modo jugable, reseteamos
      copLista = [];
      copPlayable = false;
    }

    this.setState({ listaBotones: copLista, playable: copPlayable });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Button onClick={() => this.jugar()}>Jugar</Button>
          <br />
          <Botonera listaBotones={this.state.listaBotones} playable={this.state.playable} />
        </header>
      </div>
    );
  }
}

export default App;
