import './App.css';
import { Component } from 'react';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      euros: 0,
      dolares: 0,
      forumula: 1.20,
    };
  }

  aumentar() {
    let aux = this.state.euros + 1
    this.setState({ euros: aux });
  }

  disminuir() {
    let aux = this.state.euros - 1
    if(aux>=0){
      this.setState({ euros: aux });
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <h2>{this.state.euros} EUROS</h2>
            <Botonera f1={()=>this.aumentar()} texto1={"+"} f2={()=>this.aumentar()} texto2={"-"} />
            <div>Equivale a {Math.round(this.state.euros*this.state.forumula*100)/100} DÃ³lares</div>
          </div>
        </header>
      </div>
    );
  }
}

function Botonera(props){
  return(
    <div>
      <button onClick={props.f1}>{props.texto1}</button>
      <button onClick={props.f2}>{props.texto2}</button>
    </div>
  );
}

export default App;
