import React, { Component } from 'react';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
const MapaBotones = (props) => {
  let lista = []
  for (let i = 0; i < props.listaBotones.length; i++) {
    let lista2=[]
    for (let j = 0; j < props.listaBotones.length; j++) {
      lista2.push(<><Button key={i} color={props.listaBotones[i][j].color} outline={props.listaBotones[i][j].outline}/></>)
    }
    lista.push(<>{lista2}<br/></>)
  }
  return({lista});
}
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listaBotones: Array(9).fill(null),
      // no se puede modificar el state
    }
  }
  clica(x, y) {
    // x se supone que la columna, y la fila
  }
  componentWillMount() {
    // aquí es donde creo las nueve columnas con los datos iniciales.
    let copia = this.state.listaBotones;
    for (let i = 0; i < copia.length; i++) {
      let lista2 = [];
      for (let j = 0; j < 9; j++) {
        lista2.push({outline:true,color:"success"});
      }
      copia[i].push(lista2) ;
    }
    this.setState({listaBotones:copia});
  }
  render() {
    return (
      <div className="App">
        <h1> BUCHACA </h1>
        <MapaBotones listaBotones={this.state.listaBotones} />
      </div>
    );
  }
}
export default App;
