import React, { Component } from 'react';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Botonera(props) {
    let lista = [];
    for (let i = 0; i < props.listaBotones.length; i++) {
      for (let j = 0; j < props.listaBotones[i].length; j++) {
        lista.push(<Button key={i*10+j} onClick={()=>props.clicar(i,j)} color={props.listaBotones[i][j].color} pulsado={props.listaBotones[i][j].pulsado}/>)
      }
      lista.push(<br/>)      
    }
    return <>{lista}</>;
  
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listaBotones: [], // Inicializamos como array vacío
      colores:["success","danger", "warning","secondary"]
    };
  }

  componentDidMount() {
    let copiaLista = []

    for (let i = 0; i < 9; i++) {
      let lista =[]
      for (let j = 0; j < 9; j++) {
        lista.push({color:"primary", pulsado:false});
      }
      copiaLista.push(lista)
    }

    this.setState({listaBotones:copiaLista});
  }
  

  clicar(i, j) {
    let copia = this.state.listaBotones;
  
    // Marca el botón inicial como pulsado
    copia[i][j].pulsado = true;
  
    // Reinicia todos los colores a "primary"
    for (let i = 0; i < copia.length; i++) {
      for (let j = 0; j < copia[i].length; j++) {
        copia[i][j].color = "primary";
      }
    }
  
    const colores = this.state.colores; // Referencia para los colores
    let contador = 0; // Control del índice de colores
  
    // Función para revisar vecinos
    const revisarVecinos = (i, j, color) => {
      // Definir las posiciones de los vecinos
      const vecinos = [
        [i - 1, j], // Arriba
        [i + 1, j], // Abajo
        [i, j - 1], // Izquierda
        [i, j + 1], // Derecha
      ];
  
      for (const [vi, vj] of vecinos) {
        // Verifica si está dentro del rango y pulsado
        if (
          vi >= 0 &&
          vj >= 0 &&
          vi < copia.length &&
          vj < copia[vi].length &&
          copia[vi][vj].pulsado &&
          copia[vi][vj].color === "primary"
        ) {
          copia[vi][vj].color = color; // Asigna el mismo color
          revisarVecinos(vi, vj, color); // Revisa los vecinos de esta casilla
        }
      }
    };
  
    // Asigna colores comenzando desde la casilla seleccionada
    let flag = true;
    while (flag) {
      flag = false;
  
      for (let i = 0; i < copia.length; i++) {
        for (let j = 0; j < copia[i].length; j++) {
          if (copia[i][j].pulsado && copia[i][j].color === "primary") {
            // Asigna color
            copia[i][j].color = colores[contador];
            // Revisa vecinos
            revisarVecinos(i, j, colores[contador]);
  
            // Controla el ciclo de colores
            if (contador === colores.length - 1) {
              contador = 0;
            } else {
              contador++;
            }
  
            flag = true;
          }
        }
      }
    }
  
    this.setState({ listaBotones: copia });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Botonera clicar={(i,j)=>this.clicar(i,j)} listaBotones={this.state.listaBotones} />
        </header>
      </div>
    );
  }
}

export default App;
